import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useAuth } from '../context/AuthContext';

const AdminDashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [statsRes, ticketsRes] = await Promise.all([
        axios.get('http://localhost:5000/api/users/stats/dashboard'),
        axios.get('http://localhost:5000/api/tickets')
      ]);

      setStats(statsRes.data);
      setTickets(ticketsRes.data.tickets);
    } catch (error) {
      toast.error('Error fetching data');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-white" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  const statusBadge = (status) => {
    switch (status) {
      case 'Completed':
      case 'Done':
        return 'success';
      case 'In Progress':
        return 'info';
      case 'Assigned':
      case 'In Review':
        return 'primary';
      case 'Reopened':
        return 'danger';
      default:
        return 'secondary';
    }
  };

  const priorityBadge = (priority) => {
    switch (priority) {
      case 'Urgent':
        return 'danger';
      case 'High':
        return 'warning';
      case 'Medium':
        return 'info';
      default:
        return 'secondary';
    }
  };

  return (
    <div>
      <h2 className="text-white mb-4">Admin Dashboard</h2>

      {/* Summary cards */}
      <div className="row g-3 mb-4">
        <div className="col-12 col-sm-6 col-xl-4">
          <div className="card bg-white shadow-sm h-100">
            <div className="card-body d-flex align-items-center justify-content-between">
              <div>
                <p className="text-muted mb-1">Total Tickets</p>
                <h3 className="mb-0">{stats?.totalTickets || 0}</h3>
              </div>
              <i className="bi bi-ticket-perforated fs-1 text-primary"></i>
            </div>
          </div>
        </div>
        <div className="col-12 col-sm-6 col-xl-4">
          <div className="card bg-white shadow-sm h-100">
            <div className="card-body d-flex align-items-center justify-content-between">
              <div>
                <p className="text-muted mb-1">Open Tickets</p>
                <h3 className="mb-0">{stats?.openTickets || 0}</h3>
              </div>
              <i className="bi bi-arrow-right-circle fs-1 text-warning"></i>
            </div>
          </div>
        </div>
        <div className="col-12 col-sm-6 col-xl-4">
          <div className="card bg-white shadow-sm h-100">
            <div className="card-body d-flex align-items-center justify-content-between">
              <div>
                <p className="text-muted mb-1">Resolved</p>
                <h3 className="mb-0">{(stats?.totalTickets ?? 0) - (stats?.openTickets ?? 0)}</h3>
              </div>
              <i className="bi bi-check-circle fs-1 text-success"></i>
            </div>
          </div>
        </div>
      </div>

      <div className="row g-3">
        <div className="col-12">
          <div className="card bg-white shadow-sm mb-3">
            <div className="card-header d-flex align-items-center justify-content-between">
              <h5 className="mb-0">Recent Tickets</h5>
              <span className="text-muted small">Latest 10</span>
            </div>
            <div className="card-body p-0">
              <div className="table-responsive">
                <table className="table table-hover align-middle mb-0">
                  <thead className="table-light">
                    <tr>
                      <th style={{minWidth: '220px'}}>Title</th>
                      <th>Status</th>
                      <th>Priority</th>
                      <th>Assigned To</th>
                      <th>Created</th>
                      <th></th>
                    </tr>
                  </thead>
                  <tbody>
                    {tickets.slice(0, 10).map(ticket => (
                      <tr key={ticket._id}>
                        <td className="fw-semibold">{ticket.title}</td>
                        <td>
                          <span className={`badge bg-${statusBadge(ticket.status)}`}>{ticket.status}</span>
                        </td>
                        <td>
                          <span className={`badge bg-${priorityBadge(ticket.priority)}`}>{ticket.priority}</span>
                        </td>
                        <td>{ticket.assignedTo?.name || 'Unassigned'}</td>
                        <td>{new Date(ticket.createdAt).toLocaleDateString()}</td>
                        <td className="text-end">
                          <Link to={`/tickets/${ticket._id}`} className="btn btn-sm btn-outline-primary">
                            View
                          </Link>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
