import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useAuth } from '../context/AuthContext';

const AdminDashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [statsRes, ticketsRes, usersRes] = await Promise.all([
        axios.get('http://localhost:5000/api/users/stats/dashboard'),
        axios.get('http://localhost:5000/api/tickets'),
        axios.get('http://localhost:5000/api/users')
      ]);

      setStats(statsRes.data);
      setTickets(ticketsRes.data.tickets);
      setUsers(usersRes.data);
    } catch (error) {
      toast.error('Error fetching data');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-white" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div>
      <h2 className="text-white mb-4">Admin Dashboard</h2>

      <div className="row mb-4">
        <div className="col-md-3">
          <div className="card bg-white shadow-sm">
            <div className="card-body text-center">
              <i className="bi bi-ticket-perforated fs-1 text-primary"></i>
              <h3 className="mt-2">{stats?.totalTickets || 0}</h3>
              <p className="text-muted mb-0">Total Tickets</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-white shadow-sm">
            <div className="card-body text-center">
              <i className="bi bi-arrow-right-circle fs-1 text-warning"></i>
              <h3 className="mt-2">{stats?.openTickets || 0}</h3>
              <p className="text-muted mb-0">Open Tickets</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-white shadow-sm">
            <div className="card-body text-center">
              <i className="bi bi-check-circle fs-1 text-success"></i>
              <h3 className="mt-2">{stats?.totalTickets - stats?.openTickets || 0}</h3>
              <p className="text-muted mb-0">Resolved</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-white shadow-sm">
            <div className="card-body text-center">
              <i className="bi bi-people fs-1 text-info"></i>
              <h3 className="mt-2">{users.length}</h3>
              <p className="text-muted mb-0">Total Users</p>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-md-8">
          <div className="card bg-white shadow-sm mb-3">
            <div className="card-header">
              <h5 className="mb-0">Recent Tickets</h5>
            </div>
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-sm">
                  <thead>
                    <tr>
                      <th>Title</th>
                      <th>Status</th>
                      <th>Priority</th>
                      <th>Assigned To</th>
                      <th>Created</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {tickets.slice(0, 10).map(ticket => (
                      <tr key={ticket._id}>
                        <td>{ticket.title}</td>
                        <td>
                          <span className={`badge bg-${ticket.status === 'Completed' ? 'success' : 'primary'}`}>
                            {ticket.status}
                          </span>
                        </td>
                        <td>
                          <span className={`badge bg-${ticket.priority === 'Urgent' ? 'danger' : 'warning'}`}>
                            {ticket.priority}
                          </span>
                        </td>
                        <td>{ticket.assignedTo?.name || 'Unassigned'}</td>
                        <td>{new Date(ticket.createdAt).toLocaleDateString()}</td>
                        <td>
                          <Link to={`/tickets/${ticket._id}`} className="btn btn-sm btn-outline-primary">
                            View
                          </Link>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        <div className="col-md-4">
          <div className="card bg-white shadow-sm mb-3">
            <div className="card-header">
              <h6 className="mb-0">Users</h6>
            </div>
            <div className="card-body">
              <ul className="list-group list-group-flush">
                {users.map(user => (
                  <li key={user._id} className="list-group-item d-flex justify-content-between align-items-center">
                    <div>
                      <strong>{user.name}</strong>
                      <br />
                      <small className="text-muted">{user.role}</small>
                    </div>
                    <span className="badge bg-secondary">{user.email}</span>
                  </li>
                ))}
              </ul>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
