import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useAuth } from '../context/AuthContext';

const AdminDashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState(null);
  const [tickets, setTickets] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [statsRes, ticketsRes] = await Promise.all([
        axios.get('http://localhost:5000/api/users/stats/dashboard'),
        axios.get('http://localhost:5000/api/tickets')
      ]);

      setStats(statsRes.data);
      setTickets(ticketsRes.data.tickets);
    } catch (error) {
      toast.error('Error fetching data');
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="text-center py-5">
        <div className="spinner-border text-white" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div>
      <h2 className="text-white mb-4">Admin Dashboard</h2>

      <div className="row mb-4">
        <div className="col-md-3">
          <div className="card bg-white shadow-sm">
            <div className="card-body text-center">
              <i className="bi bi-ticket-perforated fs-1 text-primary"></i>
              <h3 className="mt-2">{stats?.totalTickets || 0}</h3>
              <p className="text-muted mb-0">Total Tickets</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-white shadow-sm">
            <div className="card-body text-center">
              <i className="bi bi-arrow-right-circle fs-1 text-warning"></i>
              <h3 className="mt-2">{stats?.openTickets || 0}</h3>
              <p className="text-muted mb-0">Open Tickets</p>
            </div>
          </div>
        </div>
        <div className="col-md-3">
          <div className="card bg-white shadow-sm">
            <div className="card-body text-center">
              <i className="bi bi-check-circle fs-1 text-success"></i>
              <h3 className="mt-2">{(stats?.totalTickets ?? 0) - (stats?.openTickets ?? 0)}</h3>
              <p className="text-muted mb-0">Resolved</p>
            </div>
          </div>
        </div>
      </div>

      <div className="row">
        <div className="col-md-8">
          <div className="card bg-white shadow-sm mb-3">
            <div className="card-header">
              <h5 className="mb-0">Recent Tickets</h5>
            </div>
            <div className="card-body">
              <div className="table-responsive">
                <table className="table table-sm">
                  <thead>
                    <tr>
                      <th>Title</th>
                      <th>Status</th>
                      <th>Priority</th>
                      <th>Assigned To</th>
                      <th>Created</th>
                      <th>Actions</th>
                    </tr>
                  </thead>
                  <tbody>
                    {tickets.slice(0, 10).map(ticket => (
                      <tr key={ticket._id}>
                        <td>{ticket.title}</td>
                        <td>
                          <span className={`badge bg-${ticket.status === 'Completed' ? 'success' : 'primary'}`}>
                            {ticket.status}
                          </span>
                        </td>
                        <td>
                          <span className={`badge bg-${ticket.priority === 'Urgent' ? 'danger' : 'warning'}`}>
                            {ticket.priority}
                          </span>
                        </td>
                        <td>{ticket.assignedTo?.name || 'Unassigned'}</td>
                        <td>{new Date(ticket.createdAt).toLocaleDateString()}</td>
                        <td>
                          <Link to={`/tickets/${ticket._id}`} className="btn btn-sm btn-outline-primary">
                            View
                          </Link>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        </div>

        {/* Users card removed as requested */}
      </div>
    </div>
  );
};

export default AdminDashboard;
